mapper.select:
SELECT

FROM t_withhold_cancle
WHERE system_batch = #{batchNo,jdbcType=VARCHAR}

    and date_format(create_time, '%Y-%m-%d')  >=  str_to_date(#{tbatchDtl.startTime,jdbcType=TIMESTAMP},'%Y-%m-%d')


    and date_format(create_time, '%Y-%m-%d')  <=  str_to_date(#{tbatchDtl.endTime,jdbcType=TIMESTAMP},'%Y-%m-%d')


    and status=#{tbatchDtl.status,jdbcType=INTEGER}
