mapper.update:
update t_batch_check set
status = #{status}, check_result = #{checkResult}
where batch_id in
(

    #{item}

)

mapper.select:
select
from t_dismiss_batch dis left join t_batch_check chk on dis.id = chk.batch_id where 1 = 1
and date_format(dis.create_time, '%Y-%m-%d') >= str_to_date(#{dismissBatch.startTime,jdbcType=TIMESTAMP},'%Y-%m-%d')
and date_format(dis.create_time, '%Y-%m-%d') <= str_to_date(#{dismissBatch.endTime,jdbcType=TIMESTAMP},'%Y-%m-%d')
and dis.status=#{dismissBatch.status,jdbcType=INTEGER}
and chk.up_user_name=#{dismissBatch.upUserName,jdbcType=VARCHAR}
