mapper.select:
select  from t_batch_no where 1=1


    and create_time >= #{batchNo.startDate,jdbcType=TIMESTAMP}



    and create_time <= #{batchNo.endDate,jdbcType=TIMESTAMP}



    and batch_no=#{batchNo.batchNo,jdbcType=VARCHAR}


    and status=#{batchNo.status,jdbcType=INTEGER}


order by create_time desc
