mapper.select:
SELECT  from t_config


        and field_name = #{config.fieldName, jdbcType=VARCHAR}



        and name = #{config.name, jdbcType=VARCHAR}



        and value = #{config.value, jdbcType=VARCHAR}

mapper.update:
UPDATE t_config SET
value = #{config.value,jdbcType=VARCHAR}
where field_name = #{config.fieldName,jdbcType=VARCHAR}
